import './Post.css';
import Avatar from "@material-ui/core/Avatar"

function Post() {
    return (
        <div className = "post">
            <Avatar 
                className = "post__avatar"
                alt = "myself"
                src = "/static/images/avatar/1.jpg" />
            {/* avatar + username-> header */}
            <h3> Username</h3>
            {/* image */}
            <img className = "post__image" src = "https://st.depositphotos.com/1010338/3142/i/600/depositphotos_31420279-stock-photo-death-in-the-hood-concept.jpg" alt = " " />
            {/* username + caption */}
            <h4 className = "post__text"><strong>Vighneshshankar23: </strong>Wow that's great</h4>
        </div>
    )
}

export default Post
